import { motion } from 'framer-motion';
import { hoverState } from '../../RECOIL/ATOM/atom';
import { useRecoilState } from "recoil"

function Contents(){
    const [hover, setHover] = useRecoilState(hoverState);
    
 return(
    <>
    <div class = 'display: inline-block'>
        <span class = 'text-10px hover:underline'
        onMouseEnter={()=>setHover(true)}
        onMouseLeave={()=>setHover(false) }>스토어</span>
        <span class = 'text-10px hover:underline'
        onMouseEnter={()=>setHover(true)}
        onMouseLeave={()=>setHover(false) }>게시판</span>
        <span class = 'text-10px hover:underline'
        onMouseEnter={()=>setHover(true)}
        onMouseLeave={()=>setHover(false) }>켐페인</span></div>
    {hover && (
    <motion.div
    initial={{ opacity: 0, y: -10 }} // 초기 상태
    animate={{ opacity: 1, y: 0 }} // 호버 상태
    exit={{ opacity: 0, y: -10 }} // 사라질 때 상태
    transition={{ duration: 0.2 }} // 애니메이션 지속 시간
    className="absolute left-0 w-full shadow-lg bg-white">
    <div className="py-1" role="menu" aria-orientation="vertical" aria-labelledby="options-menu">
      <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100" role="menuitem">
        옵션 1
      </a>
      <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100" role="menuitem">
        옵션 2
      </a>
      <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100" role="menuitem">
        옵션 3
      </a>
    </div>
  </motion.div> )}</>

 );   
}

export default Contents;
